params.text:
{% set old_roles = CTX.role_table | default([]) %}
{% set new_roles = CTX.delta_table | default([]) %}
{% for new_role in new_roles %}
  {% set role_name = new_role.role_name %}
  {% set new_members = new_role.members | default([]) %}
  {% set old_role = old_roles | selectattr('role_name', 'equalto', role_name) | list | first %}
  {% set old_members = old_role.members if old_role and old_role.members else [] %}
  {% set old_ids = old_members | map(attribute='userPrincipalName', default='') | reject('equalto', '') | list %}
  {% set new_ids = new_members | map(attribute='userPrincipalName', default='') | reject('equalto', '') | list %}
  {% set added = new_ids | reject('in', old_ids) | list %}
  {% set removed = old_ids | reject('in', new_ids) | list %}
  {%- if added or removed -%}
  Role: {{ role_name }}
    {% if added %}
    🟢 Added:
      {% for upn in added %}
      - {{ upn }}
      {% endfor %}
    {% endif %}
    {% if removed %}
    🔴 Removed:
      {% for upn in removed %}
      - {{ upn }}
      {% endfor %}
    {% endif %}
  {% endif %}
{% endfor %}

{{ 
    [
        { 
            "role_name" : role.displayName,
            "members" : role.members
        }
        for role in CTX.role_data
    ]
}}
