{% set old_roles = CTX.role_table | default([]) -%}
{% set new_roles = CTX.delta_table | default([]) -%}

<style>
  .role-block {
    border: 1px solid #ccc;
    padding: 12px;
    margin-bottom: 15px;
    border-radius: 6px;
    font-family: Arial, sans-serif;
  }
  .role-name {
    font-size: 1.3em;
    font-weight: bold;
    margin-bottom: 8px;
  }
  .added {
    color: green;
    margin-left: 15px;
  }
  .removed {
    color: red;
    margin-left: 15px;
  }
  ul {
    margin-top: 4px;
    margin-bottom: 8px;
    padding-left: 20px;
  }
</style>

{% for new_role in new_roles -%}
  {% set role_name = new_role.role_name -%}
  {% set new_members = new_role.members | default([]) -%}
  {% set old_role = old_roles | selectattr('role_name', 'equalto', role_name) | list | first -%}
  {% set old_members = old_role.members if old_role and old_role.members else [] -%}
  {% set old_ids = old_members | map(attribute='userPrincipalName', default='') | reject('equalto', '') | list -%}
  {% set new_ids = new_members | map(attribute='userPrincipalName', default='') | reject('equalto', '') | list -%}
  {% set added = new_ids | reject('in', old_ids) | list -%}
  {% set removed = old_ids | reject('in', new_ids) | list -%}
  {% if added or removed -%}
  <div class="role-block">
    <div class="role-name">{{ role_name }}</div>
    {% if added -%}
      <div class="added">🟢 Added:
        <ul>
          {% for upn in added -%}
          <li>{{ upn }}</li>
          {% endfor -%}
        </ul>
      </div>
    {% endif -%}
    {% if removed -%}
      <div class="removed">🔴 Removed:
        <ul>
          {% for upn in removed -%}
          <li>{{ upn }}</li>
          {% endfor -%}
        </ul>
      </div>
    {% endif -%}
  </div>
  {% endif -%}
{% endfor -%}

{% set old_roles = CTX.role_table | default([]) -%}
{% set new_roles = CTX.delta_table | default([]) -%}

{% for new_role in new_roles -%}
  {% set role_name = new_role.role_name -%}
  {% set new_members = new_role.members | default([]) -%}
  {% set old_role = old_roles | selectattr('role_name', 'equalto', role_name) | list | first -%}
  {% set old_members = old_role.members if old_role and old_role.members else [] -%}
  {% set old_ids = old_members | map(attribute='userPrincipalName', default='') | reject('equalto', '') | list -%}
  {% set new_ids = new_members | map(attribute='userPrincipalName', default='') | reject('equalto', '') | list -%}
  {% set added = new_ids | reject('in', old_ids) | list -%}
  {% set removed = old_ids | reject('in', new_ids) | list -%}
  {% if added or removed -%}
## Role: {{ role_name }}

{% if added -%}
🟢 **Added:**
{% for upn in added -%}
- {{ upn }}
{% endfor -%}
{% endif -%}

{% if removed -%}
🔴 **Removed:**
{% for upn in removed -%}
- {{ upn }}
{% endfor -%}
{% endif -%}

{% endif -%}
{% endfor -%}

{% set old_roles = CTX.role_table | default([]) %}
{% set new_roles = CTX.delta_table | default([]) %}
{% for new_role in new_roles %}
  {% set role_name = new_role.role_name %}
  {% set new_members = new_role.members | default([]) %}
  {% set old_role = old_roles | selectattr('role_name', 'equalto', role_name) | list | first %}
  {% set old_members = old_role.members if old_role and old_role.members else [] %}
  {% set old_ids = old_members | map(attribute='userPrincipalName', default='') | reject('equalto', '') | list %}
  {% set new_ids = new_members | map(attribute='userPrincipalName', default='') | reject('equalto', '') | list %}
  {% set added = new_ids | reject('in', old_ids) | list %}
  {% set removed = old_ids | reject('in', new_ids) | list %}
  {%- if added or removed -%}
  Role: {{ role_name }}
    {% if added %}
    🟢 Added:
      {% for upn in added %}
      - {{ upn }}
      {% endfor %}
    {% endif %}
    {% if removed %}
    🔴 Removed:
      {% for upn in removed %}
      - {{ upn }}
      {% endfor %}
    {% endif %}
  {% endif %}
{% endfor %}

{{ 
    [
        {
            "role_name" : role.displayName,
            "members" : [{"userPrincipalName" : member.userPrincipalName} for member in role.members if member.userPrincipalName] | d([])
        }
        for role in CTX.role_data
    ]
}}
